mapper.select:
SELECT  A.user_id
FROM       glamp_favorite A
WHERE   A.user_id = #{userId} AND A.glamp_id = #{glampId}

SELECT      glamp_id AS glampId, room_id AS roomId ,check_in_date AS checkInDate,check_out_date AS checkOutDate
FROM       reservation_before
WHERE      glamp_id = #{glampId} AND room_id = #{roomId};

SELECT          room_image_name AS room
FROM          room_image
WHERE           room_id = #{roomId}

SELECT COUNT(review_id) AS countReviewUsers
FROM review
WHERE glamp_id = #{glampId};

SELECT         A.review_image_name
FROM          review_image A
INNER JOIN      review B
ON            A.review_id = B.review_id
WHERE          glamp_id = #{glampId}
LIMIT           #{startIdx}, #{size}

SELECT      review_image_name
FROM       review_image
WHERE       review_id = #{reviewId}

SELECT A.glamp_id AS glampId , A.glamp_name AS glampName , A.glamp_image AS glampPic , A.star_point_avg AS starPoint , A.review_count AS reviewCount
FROM glamping A JOIN room B ON A.glamp_id = B.glamp_id
A.activate_status = 1 AND B.room_id IN
#{roomId}
AND A.region = #{region}
AND (A.glamp_name like '%${searchWord}%' OR A.traffic like '%${searchWord}%')
LIMIT #{startIdx}, #{size}

SELECT COUNT(A.glamp_id) FROM glamping A JOIN room B ON A.glamp_id = B.glamp_id
A.activate_status = 1 AND B.room_id IN
#{roomId}
AND A.region = #{region}
AND (A.glamp_name like '%${searchWord}%' OR A.traffic like '%${searchWord}%')
